import randomboard = []for x in range (0,5):    board.append(["0"]*5)    def print_board(board):        for row in board:            print(" ").join(row)            print("lets play battleship")            print_board(board)def random_row(board):    from random import randint    return randint(0,len(board)-1)def random_col(board):    from random import randint    return randint(0,len(board)-1)ship_row=random_row(board)ship_col = random_col(board)print (ship_row)print(ship_col)for turn in range(4):    guess_row=input("Guess Row")    guess_col=input("Guess Col")if guess_row == ship_row and guess_col == ship_col:    print("congo! u sunk my ship")    breakpoint()else:    if turn == 3:        board[guess_row][guess_col]="X"        print_board(board)        print("game over")        print("my ship was here:[" + str(ship_row)+"]["+str(ship_col)+ "]")    else:        if(guess_row<0 or guess_row>4) or (guess_col<0 or guess_col>4):            print("oops thats not even in the ocean")        elif(board[guess_row][guess_col]== "X"):            print("u guessed one already")        else:            print("u missed my battleship")            board[guess_row][guess_col]="X"            print(turn+1)            print_board(board)