import cv2import numpy as npimport pytesseractpytesseract.pytesseract.tesseract_cmd=r"H:\ocr\tesseract.exe"img =cv2.imread("textarg.jpg")img =cv2.resize(img, None, fx=0.3, fy=0.3)gray = cv2.cvtColor(img, cv2.COLOR_BGR2GRAY)adaptive_threshold = cv2.adaptiveThreshold(gray, 255, cv2.ADAPTIVE_THRESH_GAUSSIAN_C, cv2.THRESH_BINARY, 59, 11)text = pytesseract.image_to_string(adaptive_threshold)print(text)cv2.imshow("Gray", gray)cv2.imshow("adaptive th", adaptive_threshold)cv2.waitKey(0)